mapper.insert:
insert into t_duty_gps_group (id, org_id, name, 
  share_type, sync_state, platform_id
  )
values (#{id,jdbcType=INTEGER}, #{orgId,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, 
  #{shareType,jdbcType=INTEGER}, #{syncState,jdbcType=BIT}, #{platformId,jdbcType=INTEGER}
  )

mapper.select:
select g.id, g.org_id, g.name, g.share_type, g.sync_state, g.platform_id, org.name as orgname, org.code as orgcode, org.path as orgPath from t_duty_gps_group as g left join organ as org on g.org_id=org.id where g.org_id=#{orgId,jdbcType=INTEGER}
and g.name like CONCAT("%",#{groupName,jdbcType=VARCHAR},"%")
order by g.id
limit #{pageStart,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}
