mapper.select:
select g.id, g.type_id, g.org_id, g.gps_name as gpsName, g.number as number, g.sync_state, g.platform_id , org.name as orgName, org.code as orgCode, org.path as orgPath, gt.name as typeName from t_gps as g left join organ as org on g.org_id=org.id left join t_gpstype as gt on g.type_id=gt.id where ( org.id = #{orgId,jdbcType=INTEGER} or org.code =#{orgCode,jdbcType=VARCHAR}
or org.path like CONCAT(#{orgPath,jdbcType=VARCHAR},"/",#{orgCode,jdbcType=VARCHAR},"%")
)
and g.gps_name like CONCAT("%",#{gpsname,jdbcType=VARCHAR},"%")
order by id
limit #{pageStart,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

select count(*) from t_gps as g left join organ as org on g.org_id=org.id where ( org.id = #{orgId,jdbcType=INTEGER} or org.code =#{orgCode,jdbcType=VARCHAR}
or org.path like CONCAT(#{orgPath,jdbcType=VARCHAR},"/",#{orgCode,jdbcType=VARCHAR},"%")
)
and g.gps_name like CONCAT("%",#{gpsname,jdbcType=VARCHAR},"%")
