mapper.select:
 select 
 from t_police p 
 left join t_policetype pt on pt.id = p.type_id
 left join organ o on o.id  = p.org_id
 where 
 (
 o.id = #{orgId,jdbcType=INTEGER}
  or 
  o.code =#{orgCode,jdbcType=VARCHAR}

	or o.path like CONCAT(#{orgPath,jdbcType=VARCHAR},"/",#{orgCode,jdbcType=VARCHAR},"%")

)

	and p.name like CONCAT('%','${name}','%' )  


	and pt.id = #{typeid,jdbcType=INTEGER}

 
 
	limit #{pageStart,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

select count(*) from t_police as p left join organ as org on p.org_id=org.id left join t_policetype as pt on p.type_id = pt.id where ( org.id = #{orgId,jdbcType=INTEGER} or org.code =#{orgCode,jdbcType=VARCHAR}
or org.path like CONCAT(#{orgPath,jdbcType=VARCHAR},"/",#{orgCode,jdbcType=VARCHAR},"%")
)
and p.name like CONCAT('%','${name}','%' )
and pt.id = #{typeid,jdbcType=INTEGER}

 select 
 from t_police p 
 left join t_policetype pt on pt.id = p.type_id
 left join organ o on o.id  = p.org_id
 
 	left join t_duty_police_group_member gm on gm.police_id = p.id
 	left join t_duty_police_group pg on pg.id = gm.group_id
  
 where 
 (
 o.id = #{orgId,jdbcType=INTEGER}
  or 
  o.code =#{orgCode,jdbcType=VARCHAR} 
)

	and p.name like CONCAT('%','${name}','%' )  


	and p.type_id = #{typeId,jdbcType=INTEGER}


	and pg.id = #{groupId,jdbcType=INTEGER}
