mapper.insert:
insert into t_vehicle (id, vehicle_type_id, org_id, 
  number, intercom_group, gps_id, 
  gps_name, purpose, brand, 
  site_qty, sync_state, platform_id
  )
values (#{id,jdbcType=INTEGER}, #{vehicleTypeId,jdbcType=INTEGER}, #{orgId,jdbcType=INTEGER}, 
  #{number,jdbcType=VARCHAR}, #{intercomGroup,jdbcType=VARCHAR}, #{gpsId,jdbcType=INTEGER}, 
  #{gpsName,jdbcType=VARCHAR}, #{purpose,jdbcType=VARCHAR}, #{brand,jdbcType=VARCHAR}, 
  #{siteQty,jdbcType=VARCHAR}, #{syncState,jdbcType=BIT}, #{platformId,jdbcType=VARCHAR}
  )

mapper.select:
select v.id, v.vehicle_type_id, v.org_id, v.number, v.intercom_group, v.gps_id, v.gps_name, v.purpose, v.brand, v.site_qty, v.sync_state, v.platform_id , org.name as orgname, org.code as orgcode, org.path as orgPath, gps.number as gpsnumber, vt.name as typename from t_vehicle as v left join organ as org on v.org_id=org.id left join t_gps as gps on v.gps_id=gps.id left join t_vehicletype as vt on v.vehicle_type_id=vt.id where ( org.id = #{orgId,jdbcType=INTEGER} or org.code =#{orgCode,jdbcType=VARCHAR}
or org.path like CONCAT(#{orgPath,jdbcType=VARCHAR},"/",#{orgCode,jdbcType=VARCHAR},"%")
)
and v.number like CONCAT("%",#{number,jdbcType=VARCHAR},"%")
order by id
limit #{pageStart,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

select count(*) from t_vehicle as v left join organ as org on v.org_id=org.id where ( org.id = #{orgId,jdbcType=INTEGER} or org.code =#{orgCode,jdbcType=VARCHAR}
or org.path like CONCAT(#{orgPath,jdbcType=VARCHAR},"/",#{orgCode,jdbcType=VARCHAR},"%")
)
and v.number like CONCAT("%",#{number,jdbcType=VARCHAR},"%")
