mapper.select:
  		 select 
  		 from t_weapon w 
  		 left join t_weapontype wt on wt.id = w.type_id
  		 left join organ o on o.id  = w.org_id
  		 where 
  		 (
  		 o.id = #{orgId,jdbcType=INTEGER}
  		  or 
  		  o.code =#{orgCode,jdbcType=VARCHAR}
  		
  			or o.path like CONCAT(#{orgPath,jdbcType=VARCHAR},"/",#{orgCode,jdbcType=VARCHAR},"%")
  		
  		)
  		
  			and w.number like CONCAT('%','${number}','%' )  
  		
  		  
  		
  			and w.type_id   in 

				  
  					#{item}  
 				
 				
  		
  		
  		order by w.id desc
  		 
  			limit #{pageStart,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

select count(*) from t_weapon as w left join organ as org on w.org_id=org.id left join t_weapontype as wt on w.type_id = wt.id where ( org.id = #{orgId,jdbcType=INTEGER} or org.code =#{orgCode,jdbcType=VARCHAR}
or org.path like CONCAT(#{orgPath,jdbcType=VARCHAR},"/",#{orgCode,jdbcType=VARCHAR},"%")
)
and w.number like CONCAT('%','${number}','%' )
and w.type_id in
#{item}
